import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Matches = ({ user }) => {
  const [matches, setMatches] = useState([]);
  const [teams, setTeams] = useState([]);
  const [form, setForm] = useState({ home_team_id: '', away_team_id: '', date: '', time: '', league: '', status: '', home_score: 0, away_score: 0 });
  const [editing, setEditing] = useState(null);

  const token = localStorage.getItem('token');
  const headers = { Authorization: `Bearer ${token}` };

  useEffect(() => {
    fetchMatches();
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/teams', { headers });
      setTeams(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchMatches = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/matches', { headers });
      setMatches(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editing) {
        await axios.put(`http://localhost:5000/api/matches/${editing}`, form, { headers });
      } else {
        await axios.post('http://localhost:5000/api/matches', form, { headers });
      }
      fetchMatches();
      setForm({ home_team_id: '', away_team_id: '', date: '', time: '', league: '', status: '', home_score: 0, away_score: 0 });
      setEditing(null);
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (match) => {
    setForm(match);
    setEditing(match.id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/matches/${id}`, { headers });
      fetchMatches();
    } catch (err) {
      console.error(err);
    }
  };

  const canEdit = user?.role === 'admin' || user?.role === 'editor';

  return (
    <div>
      <h1 className="text-2xl mb-4">Matches</h1>
      {canEdit && (
        <form onSubmit={handleSubmit} className="mb-4">
          <input type="number" placeholder="Home Team ID" value={form.home_team_id} onChange={(e) => setForm({ ...form, home_team_id: e.target.value })} required />
          <input type="number" placeholder="Away Team ID" value={form.away_team_id} onChange={(e) => setForm({ ...form, away_team_id: e.target.value })} required />
          <input type="date" value={form.date} onChange={(e) => setForm({ ...form, date: e.target.value })} required />
          <input type="time" value={form.time} onChange={(e) => setForm({ ...form, time: e.target.value })} required />
          <input type="text" placeholder="League" value={form.league} onChange={(e) => setForm({ ...form, league: e.target.value })} />
          <input type="text" placeholder="Status" value={form.status} onChange={(e) => setForm({ ...form, status: e.target.value })} />
          <input type="number" placeholder="Home Score" value={form.home_score} onChange={(e) => setForm({ ...form, home_score: e.target.value })} />
          <input type="number" placeholder="Away Score" value={form.away_score} onChange={(e) => setForm({ ...form, away_score: e.target.value })} />
          <button type="submit" className="bg-blue-500 text-white p-2 rounded">{editing ? 'Update' : 'Add'}</button>
        </form>
      )}
      <ul>
        {matches.map(match => (
          <li key={match.id} className="mb-2">
            {match.home_team_id} vs {match.away_team_id} - {match.date} {match.time} - {match.home_score}:{match.away_score}
            {canEdit && (
              <>
                <button onClick={() => handleEdit(match)} className="ml-2 bg-yellow-500 text-white p-1 rounded">Edit</button>
                <button onClick={() => handleDelete(match.id)} className="ml-2 bg-red-500 text-white p-1 rounded">Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Matches;
