import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Teams = ({ user }) => {
  const [teams, setTeams] = useState([]);
  const [form, setForm] = useState({ name: '', league: '', founded: '' });
  const [editing, setEditing] = useState(null);

  const token = localStorage.getItem('token');
  const headers = { Authorization: `Bearer ${token}` };

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/teams', { headers });
      setTeams(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editing) {
        await axios.put(`http://localhost:5000/api/teams/${editing}`, form, { headers });
      } else {
        await axios.post('http://localhost:5000/api/teams', form, { headers });
      }
      fetchTeams();
      setForm({ name: '', league: '', founded: '' });
      setEditing(null);
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (team) => {
    setForm(team);
    setEditing(team.id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/teams/${id}`, { headers });
      fetchTeams();
    } catch (err) {
      console.error(err);
    }
  };

  const canEdit = user?.role === 'admin' || user?.role === 'editor';

  return (
    <div>
      <h1 className="text-2xl mb-4">Teams</h1>
      {canEdit && (
        <form onSubmit={handleSubmit} className="mb-4">
          <input type="text" placeholder="Name" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required />
          <input type="text" placeholder="League" value={form.league} onChange={(e) => setForm({ ...form, league: e.target.value })} />
          <input type="number" placeholder="Founded" value={form.founded} onChange={(e) => setForm({ ...form, founded: e.target.value })} />
          <button type="submit" className="bg-blue-500 text-white p-2 rounded">{editing ? 'Update' : 'Add'}</button>
        </form>
      )}
      <ul>
        {teams.map(team => (
          <li key={team.id} className="mb-2">
            {team.name} - {team.league} - Founded: {team.founded}
            {canEdit && (
              <>
                <button onClick={() => handleEdit(team)} className="ml-2 bg-yellow-500 text-white p-1 rounded">Edit</button>
                <button onClick={() => handleDelete(team.id)} className="ml-2 bg-red-500 text-white p-1 rounded">Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Teams;
