import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Home = ({ user }) => {
  const [stats, setStats] = useState({ matches: 0 });
  const [recentMatches, setRecentMatches] = useState([]);
  const [upcomingMatches, setUpcomingMatches] = useState([]);
  const [liveMatches, setLiveMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const headers = { Authorization: `Bearer ${token}` };

  useEffect(() => {
    fetchStats();
    fetchMatches();
  }, []);

  const fetchStats = async () => {
    try {
      const [matchesRes, teamsRes, refereesRes] = await Promise.all([
        axios.get('http://localhost:5000/api/matches', { headers }),
        axios.get('http://localhost:5000/api/teams', { headers }),
        axios.get('http://localhost:5000/api/referees', { headers })
      ]);

      setStats({
        matches: matchesRes.data.length,
      });
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  };

  const fetchMatches = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/matches', { headers });
      const now = new Date();

      // Separate matches by status
      const live = response.data.filter(match =>
        match.status === 'live' || match.status === 'in_progress'
      );

      const upcoming = response.data
        .filter(match => {
          const matchDate = new Date(`${match.date} ${match.time}`);
          return matchDate > now && match.status !== 'completed';
        })
        .sort((a, b) => new Date(`${a.date} ${a.time}`) - new Date(`${b.date} ${b.time}`))
        .slice(0, 5);

      const recent = response.data
        .filter(match => match.status === 'completed')
        .sort((a, b) => new Date(b.date) - new Date(a.date))
        .slice(0, 8);

      setLiveMatches(live);
      setUpcomingMatches(upcoming);
      setRecentMatches(recent);
    } catch (err) {
      console.error('Error fetching matches:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const StatCard = ({ title, value, icon, color }) => (
    <div className={`bg-gradient-to-r ${color} text-white p-6 rounded-lg shadow-lg`}>
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">{title}</h3>
          <p className="text-3xl font-bold">{value}</p>
        </div>
        <div className="text-4xl opacity-80">{icon}</div>
      </div>
    </div>
  );

  const MatchCard = ({ match }) => (
    <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm hover:shadow-md transition-shadow">
      <div className="flex justify-between items-center mb-2">
        <span className="text-sm text-gray-500">{match.date} {match.time}</span>
        <span className={`px-2 py-1 rounded text-xs font-medium ${
          match.status === 'completed' ? 'bg-green-100 text-green-800' :
          match.status === 'upcoming' ? 'bg-blue-100 text-blue-800' :
          'bg-yellow-100 text-yellow-800'
        }`}>
          {match.status || 'Scheduled'}
        </span>
      </div>
      <div className="text-center">
        <div className="flex justify-between items-center">
          <div className="text-right flex-1 flex items-center justify-end space-x-2">
            {match.home_team_logo ? (
              <img src={`http://localhost:5000/uploads/${match.home_team_logo}`} alt={match.home_team_name} className="w-6 h-6 rounded-full" />
            ) : (
              <div className="w-6 h-6 bg-gray-300 rounded-full flex items-center justify-center text-gray-600">?</div>
            )}
            <p className="font-semibold">{match.home_team_name || `Team ${match.home_team_id}`}</p>
          </div>
          <div className="mx-4">
            <div className="text-2xl font-bold">
              {match.home_score !== null ? `${match.home_score} - ${match.away_score}` : 'vs'}
            </div>
          </div>
          <div className="text-left flex-1 flex items-center space-x-2">
            <p className="font-semibold">{match.away_team_name || `Team ${match.away_team_id}`}</p>
            {match.away_team_logo ? (
              <img src={`http://localhost:5000/uploads/${match.away_team_logo}`} alt={match.away_team_name} className="w-6 h-6 rounded-full" />
            ) : (
              <div className="w-6 h-6 bg-gray-300 rounded-full flex items-center justify-center text-gray-600">?</div>
            )}
          </div>
        </div>
      </div>
      {match.league && (
        <div className="text-center mt-2">
          <span className="text-sm text-gray-600">{match.league}</span>
        </div>
      )}
    </div>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <header className="bg-white shadow-lg border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-lg">S</span>
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">Sports Dashboard</h1>
                <p className="text-sm text-gray-500">Welcome back, {user?.username}</p>
              </div>
            </div>
            <button
              onClick={handleLogout}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg font-medium transition-colors"
            >
              Logout
            </button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Live Matches Section */}
        {liveMatches.length > 0 && (
          <div className="mb-8">
            <div className="flex items-center mb-4">
              <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse mr-2"></div>
              <h2 className="text-2xl font-bold text-gray-900">Live Matches</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {liveMatches.map(match => (
                <div key={match.id} className="bg-red-50 border-2 border-red-200 rounded-lg p-4 shadow-lg">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-sm text-red-600 font-medium">LIVE</span>
                    <span className="text-sm text-gray-500">{match.time}</span>
                  </div>
                  <div className="text-center">
                    <div className="flex justify-between items-center">
                      <div className="text-right flex-1">
                        <p className="font-semibold text-gray-900">{match.home_team_name || `Team ${match.home_team_id}`}</p>
                      </div>
                      <div className="mx-4">
                        <div className="text-2xl font-bold text-red-600">
                          {match.home_score || 0} - {match.away_score || 0}
                        </div>
                      </div>
                      <div className="text-left flex-1">
                        <p className="font-semibold text-gray-900">{match.away_team_name || `Team ${match.away_team_id}`}</p>
                      </div>
                    </div>
                  </div>
                  {match.league && (
                    <div className="text-center mt-2">
                      <span className="text-xs text-gray-600 bg-gray-100 px-2 py-1 rounded">{match.league}</span>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <StatCard
          title="Total Matches"
          value={stats.matches}
          icon="🏆"
          color="from-blue-500 to-blue-600"
        />
        </div>

        {/* Upcoming Matches */}
        {upcomingMatches.length > 0 && (
          <div className="mb-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Upcoming Matches</h2>
            <div className="bg-white rounded-lg shadow-lg p-6">
              <div className="space-y-4">
                {upcomingMatches.map(match => (
                  <div key={match.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                        <span className="text-blue-600 font-bold">⚽</span>
                      </div>
                      <div>
                        <p className="font-semibold text-gray-900">
                          {match.home_team_name || `Team ${match.home_team_id}`} vs {match.away_team_name || `Team ${match.away_team_id}`}
                        </p>
                        <p className="text-sm text-gray-500">{match.date} at {match.time}</p>
                        {match.league && <p className="text-xs text-blue-600">{match.league}</p>}
                      </div>
                    </div>
                    <div className="text-right">
                      <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-medium">
                        Upcoming
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Recent Matches */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Recent Results</h2>
          {recentMatches.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {recentMatches.map(match => (
                <MatchCard key={match.id} match={match} />
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <p className="text-gray-500">No recent matches available.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
